import React, { Component,Fragment } from 'react';
import { Input, Button, List, Typography, Divider } from "antd";


const TodoListUI = (props) => {
    return (
        <Fragment>
        <Input placeholder="Basic usage" style={{ width: 300, marginTop: 10, marginRight: 10, marginLeft: 10 }} value={props.inputValue} onChange={this.props.handleInputChange} />
        <Button type="primary" style={{ marginRight: 10 }}
            onClick={props.handleButtonClick}>提交</Button>
        <List
            style={{ marginTop: 10, marginLeft: 10, width: 400 }}
            bordered
            dataSource={this.props.list}
            renderItem={(item, index) => 
                <List.Item onClick={index => { this.props.handleDeleteItem(index) }}>
                    {item}
                /</List.Item>
            }
            />
        </Fragment>
    )   

}


export default TodoListUI
