import React, { PureComponent } from "react";
import { RecommendWrapper, RecommendItem } from "../style" 
import { connect } from "react-redux"

class Recommend extends PureComponent {
  render() {
    const {list} = this.props
    return (
      <RecommendWrapper>
        <h1>朱巧薇作品介紹與結連</h1>
        <RecommendItem>
          本網站為公開創作平台，用戶可以註冊並登入。登入後可以選擇寫文章、只看自己的文章，並對自己的文章做修改和刪除。
          <p>前端應用React、Redux</p>
          <p>後端應用Nodejs之koa框架</p>
          <p>串接MongoDB資料庫</p>
        </RecommendItem>
        <RecommendItem>
          英語新聞網站
          <p>以react.js框架串接外部API</p>
        </RecommendItem>
        <RecommendItem>
          自我介紹
          <p>使用原生Html、CSS、JavaScript</p>
          <p>BEM設計模式使命名易讀</p>
        </RecommendItem>

        <RecommendItem>
          java遊戲
          <p>多人協作，使用Bitbucket做git版控。</p>
        </RecommendItem>
        <RecommendItem>
          靜態切版
          <p>HTML、CSS切版</p>
        </RecommendItem>

        {/* {list.map((item) => {
          return (
            <RecommendItem key={item.get("id")} imgUrl={item.get("imgUrl")}></RecommendItem>
          );
        })} */}
      </RecommendWrapper>
    );
  }
}

const mapStateToProps = (state) => ({
  list: state.getIn(["home","RecommendList"])

})

export default connect(mapStateToProps,null) (Recommend);
