import styled from "styled-components";

export const DetailWrapper = styled.div`
  overflow: hidden;
  width: 620px;
  margin: 0 auto;
  padding-bottom: 100px;
  p {
    color: #ec6149;
    margin-bottom
  }

`;

export const Header = styled.div`
    margin:50px 0 20px 0;
    font-size: 34px;
    line-height:44px;
    color:#333;
    font-weight:bold;
`
export const Content = styled.div`
  color: #2f2f2f;
  margin: 20px 0;
  font-size: 16px;
  line-height: 30px;
  img {
    width: 100%;
  }
  p {
    margin: 20px 0;
  }
`;

export const Textarea = styled.textarea`
  width:99%;
  height:600px;
  color: #2f2f2f;
  margin: 20px 0;
  font-size: 16px;
  line-height: 30px;
  img {
    width: 100%;
  }
  p {
    margin: 20px 0;
  }
`;

export const Button = styled.div`
  cursor: pointer;
  width: 220px;
  height: 30px;
  line-height: 30px;
  color: #fff;
  background: #1cc283;
  border-radius: 15px;
  margin: 10px auto;
  text-align: center;
`;
