import React, { Component, Fragment } from "react";
import { Button, Modal, Input } from "antd";
import axios from "axios";
import'./style.css'

class Detail extends Component {
  constructor(props) {
      super(props);
    this.state = {
        login: false,
        modal: false,
        name: "",
        password: "",
      };
      this.clickLogin = this.showModal.bind(this)
      this.showModal
  }   
    
    componentDidMount() {
    //   axios.get("http://www.dell-lee.com/react/api/isLogin.json").then(res => {
    //       const login = res.data.data.login
    //       this.setState({ login })
    //       console.log(login)
    // })
    }

    showModal() {
        this.setState({modal:true})
    }
    
    // hideModal() {
    //     this.setState({modal:false})
    // }

    changeName() {
        this.setState({ name: this.state.name })
        console.log(this.state.name);
    }

    render() {
        const { login } = this.state
        return (
          <Fragment>
            {login ? (
              <Button type="primary">登出</Button>
            ) : (
              <Button type="primary" onClick={this.showModal}>
                登入
              </Button>
            )}
            <Modal
              title="登入"
              visible={this.state.modal}
              onOk={() => {}}
              onCancel={() => {this.hideModal();}}
            >
                    <Input placeholder="Basic usage" style={{ marginBottom: 10 }}
                        onChange={this.changeName} value={this.state.name}/>
              <Input placeholder="Basic usage" />
            </Modal>
          </Fragment>
        );       
  }
}
export default Detail;
