import React, { Component, Fragment } from 'react';
import { Button } from "antd";

class Ref extends Component {
  

    render() {
        return (
            <Fragment>
                <input ref={(ref) => this.inputElement = ref}/><br/>
                <Button type='primary' onClick= {this.getContent}>提交</Button>
            </Fragment>
            
        )
    }

}
export default Ref
