import { fromJS } from "immutable";

const defaultState = fromJS({
  topicItem: [],
  articleList: [],
  RecommendList: [],
});

export default (state = defaultState, action) => {
  switch (action.type) {
    case "change-home-data": 
      state.merge({
        topicItem:fromJS(action.topicItem),
      })
    default:
      return state;
  }
};
