import styled from "styled-components";

export const HomeWrapper = styled.div`
  overflow: hidden;
  width: 960px;
  margin: 0 auto;
`;
export const HomeLeft = styled.div`
  float: left;
  width: 625px;
  margin-left: 15px;
  padding-top: 30px;
  .banner-img {
    width: 625px;
    height: 240px;
  }
`;
export const HomeRight = styled.div`
  float: right;
  width: 280px;
`;
export const TopicWrapper = styled.div`
  overflow: hidden;
  padding: 20px 0 10px 0;
  margin-left: -18px;
  border-bottom: 1px solid #dcdcdc;
`;

export const TopicItem = styled.div`
  position: relative;
  float: left;
  height: 32px;
  line-height: 32px;
  margin-left: 18px;
  margin-bottom: 18px;
  padding-right: 10px;
  background: #eee;
  font-size: 14px;
  color: #000;
  border: 1px solid #dcdcdc;
  border-radius: 4px;
  .topic-pic {
    display: block;
    float: left;
    width: 32px;
    height: 32px;
    margin-right: 10px;
  }
`;


export const ListItem = styled.div`
height:140px;
padding: 20px 0;
border-bottom:1px solid #dcdcdc;
overflow:hidden;
position:relative;
.pic{
  display: block;
  width:125px;
  height:100px;
  float:right;
  border-radius:10px;
}

`;
export const ListInfo = styled.div`
width:500px;
height:80px;
float:left;
.title{
  line-height: 27px;
  font-size:18px;
  font-weight:bold;
  color:#333;
}
.desc {
  line-height:24px;
  font-size:13px;
  color:#999;
}
`
export const AuthorInfo = styled.div`
  width: 500px;
  line-height: 31px;
  height: 31px;
  background: #fff;
  position: absolute;
  bottom: 0;
  left: 0;
  .author,
  .date,
  .time {
    color: #b4b4b4;
  }
  .author span,
  .del,
  .edit {
    color: #ea6f5a;
  }
  .date,
  .time,
  .edit,
  .del {
    display: inline-block;
    cursor: pointer;
    float: right;
  }
  .time,
  .edit,
  .del {
    padding-right: 10px;
  }
`;

export const RecommendWrapper = styled.div`
  margin: 30px 0 ;
  width: 280px;
  h1{
    margin-bottom:20px;
  }
`
export const Introduce = styled.div`
  width: 280px;
  height: 250px;
  line-height: 20px;
  color: #999;
  p:first-child{
    margin-top:10px;
  }
  p {
    font-size: 13px;
  }
`;


export const RecommendItem = styled.div`
  width: 280px;
  height: 90px;
  line-height: 20px;
  color: #999;
  img {
    width:280px;
    height:50px;
  }
  p {
    font-size:13px;
  }
  
`;


export const WriterWrapper = styled.div`
width:278px;
border:1px solid #dcdcdc;
border-radius:3px;
height:300px;
line-height:300px;
text-align:center;
`
export const LoadMore = styled.div`
  width:100%;
  height:40px;
  line-height: 40px;
  margin: 30px 0;
  background:#a5a5a5;
  text-align:center;
  border-radius:20px;
  color:#fff;
  cursor:pointer;

`

export const BackTop = styled.div`
position:fixed;
right:100px;
bottom:100px;
width:60px;
height:60px;
line-height: 60px;
text-align:center;
border:1px solid #ccc;
font-size:14px;
cursor: pointer;
`
