import React, { Component, Fragment } from 'react';

class Ref extends Component {
    constructor(props) {
        super(props);
        this.getContent = this.getContent.bind(this);
        this.setState = {
            content:""
        }
    }


    getContent() {
        let content = this.inputElement.innerHTML
        console.log(content)
        this.setState({content})
    }

    render() {
        return (
            <Fragment>
                <input ref={(ref) => this.inputElement = ref}/>
                <button onClick= {this.getContent}>提交</button>
            </Fragment>
            
        )
    }

}
export default Ref
