import React, { Component } from "react";
import { DetailWrapper, Header, Content, Image } from "./style"
import { connect } from "react-redux"
import axios from "axios"
import { actionCreators } from "./store"
import { withRouter } from "react-router-dom"


class Detail extends Component {

  render() {
    const {title, content, imageUrl, editStatus} = this.props
    return (
      <DetailWrapper>
        <Header>{title}</Header>
        <img src={imageUrl} />
        {editStatus? null : <Content /*dangerouslySetInnerHTML={ {__html: content} }*/>{content}</Content>}       
      </DetailWrapper>
    );
  }
  componentDidMount() {
    this.props.getDetail(this.props.match.params.id)
  }

}

const mapStateToProps = (state) => ({
  title: state.getIn(["detail", "title"]),
  content: state.getIn(["detail", "content"]),
  imageUrl: state.getIn(["detail", "imageUrl"]),
  editStatus: state.getIn(["detail", "editStatus"]),
});

const mapDispatchToProps = (dispatch) => ({
  getDetail(id) {
    dispatch(actionCreators.getDetail(id))   
    }
  
})


export default connect(mapStateToProps,mapDispatchToProps)(withRouter(Detail));
