import React, { Component, Fragment} from "react";
import logo from "./logo.png"
import "./style.css"
import { Menu } from "antd";
import {
    CoffeeOutlined,
    HeartOutlined,
    AppleOutlined,
    StarOutlined,
    TranslationOutlined,
    ScheduleOutlined,
  AppstoreOutlined,
  SettingOutlined,
} from "@ant-design/icons";
import { Icon } from "@ant-design/compatible";
import axios from "axios";
import{Link} from"react-router-dom"

const iconMap = {
    1: <AppleOutlined />,
    2: <CoffeeOutlined /> ,
    3:<HeartOutlined />,
    4: <StarOutlined />, 
    5: <TranslationOutlined />,
    6:<ScheduleOutlined />
};


class AppHeader extends Component{

    constructor(props) {
        super(props);
        this.state = {
            list:[]
        }
    }
    componentDidMount() {
        axios.get('http://www.dell-lee.com/react/api/header.json').then((res) => {
            this.setState({ list: res.data.data })
        })
    }

    getMenuItems() {
        return(
        this.state.list.map(item => {
            return (
              <Link to="/id">
                <Menu.Item key={item.id} icon={iconMap[item.id]}>
                  {item.title}
                </Menu.Item>
              </Link>
            );
        })
        )
    }
        
    render() {
        return (
          <Fragment>
            <img src={logo} className="app-header-logo" />
            <Menu
              onClick={this.handleClick}
              mode="horizontal"
              className="app-header-menu"
            >
              {this.getMenuItems()}
            </Menu>
          </Fragment>
        );
    }
}

export default AppHeader
