import React,{ Component} from 'react';
import {connect} from 'react-redux'
import { CSSTransition } from "react-transition-group";
import { 
  HeaderWrapper,
  Logo,
  Nav,
  NavItem,
  NavSearch,
  Addition,
  Button,
  SearchWrapper,
  SearchInfo,
  SearchInfoTitle,
  SearchInfoSwitch,
  SearchInfoList,
  SearchInfoItem,
} from "./style";
import { actionCreator } from "./store"


class Header extends Component{

  getSearchInfo(show) {
  const { list, focused } = this.props;
    
    if (show) {
    return (
      <SearchInfo>
        <SearchInfoTitle>
          熱門搜索
          <SearchInfoSwitch>換一批</SearchInfoSwitch>
        </SearchInfoTitle>
        <SearchInfoList>
          {list.map((item) => 
             <SearchInfoItem key={item}>{item}</SearchInfoItem>)          
        }
          
        </SearchInfoList>
      </SearchInfo>
    );
  }else return null
}


  render() {
  const { focused,handleInputFocus,handleInputBlur } = this.props;
    return(
    <HeaderWrapper>
      <Logo href="/" />
      <Nav>
        <NavItem className="left active">首頁</NavItem>
        <NavItem className="left">下載APP</NavItem>
        <NavItem className="right">
          <span className="iconfont">&#xe636;</span>
        </NavItem>
        <NavItem className="right">登入</NavItem>
        <SearchWrapper>
          <CSSTransition in={focused} timeout={200} classNames="slide">
            <NavSearch
              className={focused ? "focused" : ""}
              onFocus={handleInputFocus}
              onBlur={handleInputBlur}
            ></NavSearch>
          </CSSTransition>
          <span className={focused ? "iconfont focused" : "iconfont"}>
            &#xe721;
          </span>
          {this.getSearchInfo(focused)}
        </SearchWrapper>
      </Nav>
      <Addition>
        <Button className="reg">註冊</Button>
        <Button className="writting">
          <span className="iconfont">&#xe624;</span>寫文章
        </Button>
      </Addition>
    </HeaderWrapper>
  );
    
  }
}

//將取到的state值映射到props
const mapStateToProps = (state) => {
    return {
      focused: state.getIn(['header', 'focused']),
      list: state.getIn(['header', 'list']),
      pa
    }
}
//使prop中的方法能調用dispatch
const mapDispatchToProps = (dispatch) => {
  return {
    handleInputFocus() {
      dispatch(actionCreator.getheaderList());
      dispatch(actionCreator.searchFocus())
    },
    handleInputBlur() {
      dispatch(actionCreator.searchBlur())
    }
    
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(Header);
