import React, { Component } from 'react';
import {connect} from 'react-redux'
import { CSSTransition } from "react-transition-group";
import {
  HeaderWrapper,
  Logo,
  Nav,
  NavItem,
  NavSearch,
  Addition,
  Button,
  SearchWrapper,
} from "./style";


class Header extends Component {



    handleInputFocus() {
        this.setState({focused: true});
    }

    handleInputBlur() {
        this.setState({ focused: false });

    }


    render() {
        return (
          <HeaderWrapper>
            <Logo href="/" />
            <Nav>
              <NavItem className="left active">首頁</NavItem>
              <NavItem className="left">下載APP</NavItem>
              <NavItem className="right">
                <span className="iconfont">&#xe636;</span>
              </NavItem>
              <NavItem className="right">登入</NavItem>
              <SearchWrapper>
                        <CSSTransition
                            in={this.props.focused}
                            timeout={200}
                            classNames="slide"
                            >
                  <NavSearch
                    className={this.props.focused ? "focused" : ""}
                    onFocus={this.props.handleInputFocus}
                    onBlur={this.props.handleInputBlur}
                  ></NavSearch>
                </CSSTransition>
                <span
                  className={
                    this.props.focused ? "iconfont focused" : "iconfont"
                  }
                >
                  &#xe721;
                </span>
              </SearchWrapper>
            </Nav>
            <Addition>
              <Button className="reg">註冊</Button>
              <Button className="writting">
                <span className="iconfont">&#xe624;</span>寫文章
              </Button>
            </Addition>
          </HeaderWrapper>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        focused: state.focused 
    }
}

const mapDispatchToProps = (dispatch) => {
  return {
    handleInputFocus() {
      const action = {
        type: 'search_focus'
      }
      dispatch(action)
    }
    
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
