import React, { PureComponent } from "react";
import { RecommendWrapper, RecommendItem, Introduce } from "../style" 
import { connect } from "react-redux"
import site1 from "../../../statics/site1.png"
import site2 from "../../../statics/site2.png";
import site3 from "../../../statics/site3.png";
import site4 from "../../../statics/site4.png";


class Recommend extends PureComponent {
  render() {
    const {list} = this.props
    return (
      <RecommendWrapper>
        <h1>朱巧薇作品介紹與連結</h1>
        <Introduce>
          本網站為公開創作平台，用戶可以註冊並登入。登入後可以寫文章、只看自己的文章，並對自己的文章做修改和刪除。
          <p>前端使用React、Redux</p>
          <p>後端使用Node.js的koa框架</p>
          <p>串接MongoDB資料庫</p>
          <p>使用nginx，上傳到GCP</p>
        </Introduce>
        <RecommendItem>
          <img src={site1}></img>
          <p>以react.js框架串接外部API</p>
        </RecommendItem>
        <RecommendItem>
          <img src={site2}></img>

          <p>原生Html、CSS、JavaScript製作動畫</p>
          <p>BEM設計模式使命名易讀</p>
        </RecommendItem>
        <RecommendItem>
          <img src={site3}></img>

          <p>HTML、CSS切版</p>
        </RecommendItem>
        <RecommendItem>
          <img src={site4}></img>
          <p>多人協作，使用Bitbucket做git版控。</p>
        </RecommendItem>

        {/* {list.map((item) => {
          return (
            <RecommendItem key={item.get("id")} imgUrl={item.get("imgUrl")}></RecommendItem>
          );
        })} */}
      </RecommendWrapper>
    );
  }
}

const mapStateToProps = (state) => ({
  list: state.getIn(["home","RecommendList"])

})

export default connect(mapStateToProps,null) (Recommend);
