import React, { PureComponent } from "react";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import { LoginWrapper, LoginBox, Input, Button,Textarea,Dialog } from "./style";
import { actionCreators } from "./store";

class Write extends PureComponent {
  
  render() {
    const { postStatus ,postArticle } = this.props;
    // if (loginStatus) {
      return (
        <LoginWrapper>
          <LoginBox>
            <Input
              placeholder="標題"
              ref={(dom) => {
                this.title = dom;
              }}
            />
            <Input
              placeholder="欲插入之圖片網址 http://............"
              ref={(dom) => {
                this.imgUrl = dom;
              }}
            />
            <Textarea
              placeholder="文章內容"
              ref={(dom) => {
                this.content = dom;
              }}
            />
            <Button
              onClick={() =>
                postArticle(
                  this.title.value,
                  this.content.value,
                  this.imgUrl.value
                )
              }
            >
              發表文章
            </Button>
            
            <Dialog>發表失敗，再試一次</Dialog>
          </LoginBox>
        </LoginWrapper>
    );
    
    if (postStatus == 1) {
      return <Redirect to="/" />;
    }
    // } else {
    //   return <Redirect to="/" />;
    // }
  }
}

const mapStateToProps = (state) => ({
  postStatus: state.getIn(["write", "postStatus"]),
});

const mapDispatchToProps = (dispatch) => ({
  postArticle(title, content, imgUrl) {
    dispatch(actionCreators.postArticle(title, content, imgUrl))
  }
});

export default connect(mapStateToProps, mapDispatchToProps)(Write);
