import React, { Component } from "react";
import { DetailWrapper, Header, Content, Image, Textarea, Button, Au } from "./style"
import { connect } from "react-redux"
import axios from "axios"
import { actionCreators } from "./store"
import { withRouter } from "react-router-dom"
import { AuthorInfo } from "../home/style";


class Detail extends Component {

  render() {
    const { login, account, title, content, imageUrl, username, editStatus, editArticle, changeDetailEditStatus } = this.props;
    return (
      <DetailWrapper>
        <Header>{title}</Header>
        {/* {login && accout} */}
        <AuthorInfo>
          <span
            onClick={() => {
              changeDetailEditStatus(true);
            }}
          >
            {" "}
            編輯文章
          </span>
        </AuthorInfo>

        <img src={imageUrl} />
        {editStatus ? (
          <Textarea
            ref={(dom) => {
              this.contentValue = dom;
            }}
          >
            {content}
          </Textarea>
        ) : (
          <Content /*dangerouslySetInnerHTML={ {__html: content} }*/>
            {content}
          </Content>
        )}
        {editStatus ? (
          <Button
            onClick={() => {
              editArticle(this.props.match.params.id, this.contentValue.value);
            }}
          >
            修改完成
          </Button>
        ) : null}
      </DetailWrapper>
    );
  }
  componentDidMount() {
    this.props.getDetail(this.props.match.params.id)
  }

}

const mapStateToProps = (state) => ({
  title: state.getIn(["detail", "title"]),
  content: state.getIn(["detail", "content"]),
  imageUrl: state.getIn(["detail", "imageUrl"]),
  username: state.getIn(["detail", "username"]),
  editStatus: state.getIn(["detail", "editStatus"]),
  login: state.getIn(["login", "login"]),
  account: state.getIn(["login", "account"]),
});

const mapDispatchToProps = (dispatch) => ({
  getDetail(id) {
    dispatch(actionCreators.getDetail(id))   
  },
  editArticle(id, content) {
    dispatch(actionCreators.editArticle(id, content))
  },
  changeDetailEditStatus(editStatus) {
    dispatch(actionCreators.changeDetailEditStatusAction(editStatus));
  },
})


export default connect(mapStateToProps,mapDispatchToProps)(withRouter(Detail));
