import React, { PureComponent } from "react";
import { RecommendWrapper, RecommendItem, Introduce, Scratch } from "../style" 
import { connect } from "react-redux"
import site1 from "../../../statics/site1.png"
import site2 from "../../../statics/site2.png";
import site3 from "../../../statics/site3.png";
import site4 from "../../../statics/site4.png";
import site5 from "../../../statics/site5.png";



class Recommend extends PureComponent {
  render() {
    const {list} = this.props
    return (
      <RecommendWrapper>
        <h1>本站操作說明</h1>
        <Introduce>
          本站為眾人創作平台，大部分功能需要用戶註冊並登入。登入後可選擇只看自己的文章或全部文章，寫下新文章，文章列表中可以刪除自己的文章，進入文章後可以編輯自己的文章。
          <p>技術要點：</p>
          <p>前端使用React、Redux</p>
          <p>後端使用Node.js的Koa框架</p>
          <p>串接MongoDB資料庫</p>
          <p>使用Nginx與GCP雲端主機</p>
        </Introduce>
        <RecommendItem>
          <a href="http://35.229.221.230:82/">
            <img src={site1}></img>
          </a>
          <p>以React.js框架製作，串接外部API</p>
        </RecommendItem>
        <RecommendItem>
          <a href="http://35.229.221.230:81/">
            <img src={site2}></img>
          </a>
          <p>原生Html、CSS、JavaScript製作動畫</p>
          <p>BEM設計模式使命名易讀</p>
        </RecommendItem>
        <RecommendItem>
          <a href="http://35.229.221.230:81/cssForme/cssWork.html">
            <img src={site3}></img>
          </a>
          <p>HTML、CSS切版</p>
        </RecommendItem>
        <RecommendItem>
          <a href="http://35.229.221.230:81/javaGame/index.html">
            <img src={site4}></img>
          </a>
          <p>以Java與他人合作開發之遊戲，用Bitbucket做git版本控制。</p>
        </RecommendItem>
        <RecommendItem>
          <a href="https://scratch.mit.edu/projects/351789399/">
            <img src={site5}></img>
          </a>
          <p>以程式思維做的完整Scartch遊戲</p>
        </RecommendItem>

        {/* {list.map((item) => {
          return (
            <RecommendItem key={item.get("id")} imgUrl={item.get("imgUrl")}></RecommendItem>
          );
        })} */}
      </RecommendWrapper>
    );
  }
}

const mapStateToProps = (state) => ({
  list: state.getIn(["home","RecommendList"])

})

export default connect(mapStateToProps,null) (Recommend);
