import React, { Component } from 'react'
import { HomeWrapper, HomeLeft, HomeRight } from './style'
import List from './components/List'
import Topic from './components/Topic'
import Writer from './components/Writer'
import Recommend from './components/Recommend'
import { connect } from 'react-redux'
import { actionCreators } from './store'
import {BackTop} from './style'

class Home extends Component {

  handleScrollTop() {
    window.scrollTo(0, 0)
  }

  render() {
    return (
      <HomeWrapper>
        <HomeLeft>
          <img
            alt=""
            className="banner-img"
            src="https://scontent.ftpe4-2.fna.fbcdn.net/v/t1.0-9/119928261_3504183522977112_6727443703106507929_o.jpg?_nc_cat=109&_nc_sid=730e14&_nc_ohc=1raVg3eQrgUAX9oWkrw&_nc_ht=scontent.ftpe4-2.fna&oh=12fcb73213597a45232c7ca695ba3e64&oe=5F972BDB"
            alt=""
          ></img>
          <Topic></Topic>
          <List></List>
        </HomeLeft>
        <HomeRight>
          <Recommend></Recommend>
          <Writer></Writer>
        </HomeRight>
        {this.props.showScroll ? <BackTop onClick={this.handleScrollTop}>回到頂部</BackTop> : null}
       
      </HomeWrapper>
    );
  }
  componentDidMount() {
    this.props.changeHomeData();
    this.bindEvents();
  }

  componentWillUnmount(){}

  bindEvents() {
    window.addEventListener('scroll',this.props.changeScrollTopShow)
  }

}

const mapStateToProps = (state) => ({
  showScroll: state.getIn(["home","showScroll"])
})

const mapDispatchToProps = (dispatch) => ({
  
    changeHomeData() {
      dispatch(actionCreators.getHomeInfo())
  },
  
  changeScrollTopShow() {
    if (document.documentElement.scrollTop > 50) {
      dispatch(actionCreators.toggleTopShow(true))
    } else {
      dispatch(actionCreators.toggleTopShow(false))
    }
    }
  })

export default connect(mapStateToProps, mapDispatchToProps)(Home);
