import React, { Component } from 'react';
import {
  HeaderWrapper,
  Logo,
  Nav,
  NavItem,
  NavSearch,
  Addition,
  Button,
  SearchWrapper,
} from "./style";


class Header extends Component {

    constructor(props) {
        super(props);
        this.state = {
            focused: false,
        }
    }



    render() {
        return (
          <HeaderWrapper>
            <Logo href="/" />
            <Nav>
              <NavItem className="left active">首頁</NavItem>
              <NavItem className="left">下載APP</NavItem>
              <NavItem className="right">
                <span className="iconfont">&#xe636;</span>
              </NavItem>
              <NavItem className="right">登入</NavItem>
              <SearchWrapper>
                <NavSearch
                            className={this.state.focused ? "focused" : ""}
                            onFocus={this.handleInputFocus}
                ></NavSearch>
                        <span className={this.state.focused ? "iconfont focused" : "iconfont"}>&#xe721;</span>
              </SearchWrapper>
            </Nav>
            <Addition>
              <Button className="reg">註冊</Button>
              <Button className="writting">
                <span className="iconfont">&#xe624;</span>寫文章
              </Button>
            </Addition>
          </HeaderWrapper>
        );
    }
}

export default Header
