import React, { Component, Fragment } from "react";
import "antd/dist/antd.css";
import { Input, Button, List, Typography, Divider } from "antd";
import store from "./store"
import { CHANGE_INPUT_VALUE, ADD_TODO_ITEM, DELETE_TODO_ITEM } from "./store/actionTypes.js"
imp



class TodoList extends Component {
  constructor(props) {
    super(props);
    this.state = store.getState()
    this.handleInputChange = this.handleInputChange.bind(this)
    this.handleStoreChange = this.handleStoreChange.bind(this)
    this.handleButtonClick = this.handleButtonClick.bind(this)
    //只要store的state改變就觸發
    store.subscribe(this.handleStoreChange)
  }

  handleInputChange(e) {
    const action = {
      type: CHANGE_INPUT_VALUE,
      value: e.target.value
    }
      store.dispatch(action)
  }

  handleStoreChange() {
    this.setState(store.getState())
  }

  handleButtonClick() {
    const action = {
      type : ADD_TODO_ITEM,
      value : this.state.value
    }
    store.dispatch(action)

  }
  handleDeleteItem(index) {
    const action = {
      type: DELETE_TODO_ITEM,
      index
    }
    store.dispatch(action)
  }

    render() {
        return (
          <Fragment>
            <Input placeholder="Basic usage" style={{ width: 300, marginTop: 10, marginRight: 10, marginLeft: 10 }} value={this.state.inputValue} onChange={this.handleInputChange}/>
            <Button type="primary" style={{ marginRight: 10 }}
             onClick={this.handleButtonClick}>提交</Button>
            <List
              style={{ marginTop: 10, marginLeft: 10 ,width:400 }}
              bordered
              dataSource={this.state.list}
              renderItem={(item,index) => (
                <List.Item onClick={this.handleDeleteItem.bind(this,index)}>
                   {item}
                </List.Item>
              )}
            />
          </Fragment>
        );
    }
}

export default TodoList
